(ns advent.day1)

(declare data)

(defn as-num [c]
  (condp = c
      \( 1
      \) -1))

(->> data
     (map as-num)
     (reduce +))

(->> data
     (map as-num)
     (reductions +)
     (take-while (complement neg?))
     count
     inc)


